mapper.select:
select count(gid) from goodsinfo

	
		and sid = #{sid}
	
	
	
		and gname like concat('%', #{gname}, '%')
	
	
	
		and status = #{status}

select gid, gname, sid, price, rebate, date_format(sdate, '%Y-%m-%d %H:%i') sdate,
	date_format(edate, '%Y-%m-%d %H:%i') edate, status from goodsinfo

	
		and sid = #{sid}
	
	
	
		and gname like concat('%', #{gname}, '%')
	
	
	
		and status = #{status}
