mapper.select:
select count(sid) from shopinfo

	
		and tid=#{tid}
	
	
	
		and sname like concat('%', #{sname}, '%')
	
	
	
		and status=#{status}

select sid, sname, tid, city, area, tel, date_format(regDate, '%Y-%m-%d %H:%i') regDate, status from shopinfo

	
		and tid=#{tid}
	
	
	
		and sname like concat('%', #{sname}, '%')
	
	
	
		and status=#{status}
	

order by sid limit #{page}, #{rows}
